import React, { useEffect, useRef, useState } from 'react';
import { observer } from 'mobx-react-lite';
import canvasState from '../store/canvasState';
import toolState from '../store/toolState'
import Brush from '../tools/Brush'
import '../styles/canvas.scss'
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import { useParams } from 'react-router-dom';

const Canvas = observer(() => {
    const [show, setShow] = useState(true);
    const usernameRef = useRef()
    const canvasRef = useRef();
    const params = useParams()
    useEffect(() => {
        canvasState.setCanvas(canvasRef.current);
        toolState.setTool(new Brush(canvasRef.current));
    }, []);
    useEffect(() => {
        if (canvasState.username) {
            console.log('Attempting to open WebSocket connection...');  // Debug log for WebSocket connection attempt
            socket = new WebSocket(`ws://localhost:5000`);

            socket.onopen = () => {
                console.log(`Connection established: ${canvasState.username}`);
                socket.send(JSON.stringify({
                    id: params.id,
                    username: canvasState.username,
                    method: 'connection'
                }));
            };

            socket.onmessage = (event) => {
                console.log('Message received:', event.data);  // Debug log for incoming messages
                const msg = JSON.parse(event.data);
                switch (msg.method) {
                    case 'connection':
                        console.log(`User ${msg.username} connected`);
                        break;
                    default:
                        console.log('Unknown method:', msg.method);  // Debug log for unknown methods
                        break;
                }
            };

            socket.onerror = (error) => {
                console.error('WebSocket error:', error);  // Debug log for WebSocket errors
            };

            socket.onclose = (event) => {
                console.log('WebSocket connection closed:', event);  // Debug log for closing WebSocket
            };

            return () => {
                socket.close();
                console.log('WebSocket connection closed');  // Debug log for closing WebSocket on component unmount
            };
        }
    }, [canvasState.username]);
    
    const mouseDownHandler = () => {
        canvasState.pushToUndo(canvasRef.current.toDataURL())
    }

    const connectHandler = () => {
        setShow(false)
        canvasState.setUsername(usernameRef.current.value)
    }

    
    return (
        <div className="canvas">
            <Modal show={show} onHide={()=>{}}>
                <Modal.Header>
                <Modal.Title>Enter your name</Modal.Title>
                </Modal.Header>
                <Modal.Body><input type="text" ref={usernameRef}/></Modal.Body>
                <Modal.Footer>
                <Button variant="secondary" onClick={()=>connectHandler()}>
                    Enter
                </Button>
                
                </Modal.Footer>
            </Modal>
            <canvas onMouseDown={()=> mouseDownHandler ()} ref={canvasRef} width={600} height={400} />
        </div>
    );
});

export default Canvas
