import React from 'react'
import '../styles/toolbar.scss'

const SettingBar = () => {
  return (
    <div className="settingbar">
      <label htmlFor='line-width'>Line width</label>
      <input 
        onChange={()=>toolState.setLineWidth(e.target.current)}
        style={{marginLeft:'0 10px 0 10px'}} 
        id={line-width}   
        type="number" 
        defaultValue={1} 
        min={1} 
        max={50}
        />
    </div>
  )
}

export default SettingBar
