import { makeAutoObservable } from "mobx";

class ToolState{
    tool= null
    constructor(){
        makeAutoObservable(this)
    }
    setTool(tool){
        this.tool = tool
    }
    setFillColor(color){
        this.fillColor= color
    }
    setStrokeColor(color){
        this.strokeColor= color
    }
    setLineWidth(width){
        this.width= width
    }
   
}

export default new ToolState();
