import Tool from './Tool'

export default class Circle extends Tool{
    constructor(canvas){
        super(canvas)
        this.listen()
    }
    listen(){
        this.canvas.onmousemove=this.mouseMoveHandler.bind(this)
        this.canvas.onmouseup=this.mouseUpHandler.bind(this)
        this.canvas.onmousedown=this.mouseDownHandler.bind(this)
    }

    mouseUpHandler(e){
        this.mouseDown = false
    }
    mouseDownHandler(e){
        this.mouseDown = true
        this.ctx.beginPath()
        this.startX = e.pageX-e.target.offsetLeft;
        this.startY =  e.pageY-e.target.offsetTop;
        this.saved = this.canvas.toDataURL()
    }
    mouseMoveHandler(e){
        if(this.mouseDown){
            let curentX =  e.pageX-e.target.offsetLeft
            let curentY =  e.pageY-e.target.offsetTop
            let width = curentX-this.startX
            let height = curentY-this.startY
            let r = Math.sqrt(width**2 + height**2)
            this.draw(this.startX, this.startY, r)
        }
    }
    draw(x, y, w ,h){
        this.ctx.beginPath()
// this.ctx.ellipse(x,y,w,h, 6, 0, 6);
// this.ctx.fill();
// this.ctx.stroke();
        const img = new Image()
        img.src = this.saved
        img.onload = async () =>{
            this.ctx.clearRect(0,0, this.canvas.width, this.canvas.height)
            this.ctx.drawImage(img, 0, 0, this.canvas.width, this.canvas.height)
            this.ctx.beginPath()
            this.ctx.ellipse(x, y, Math.abs(w / 2), Math.abs(h / 2), 0, 0, 2 * Math.PI);
            this.ctx.fill();
            this.ctx.stroke();
        }
      
    }
}
